mapper.select:
SELECT memo_id, diary_name, diary_content, diary_image, created_at
FROM diary
WHERE member_no_login = #{memberNoLogin}
ORDER BY created_at DESC
LIMIT #{offset}, #{pageSize}

SELECT memo_id, diary_name, diary_content, diary_image, created_at
FROM diary
WHERE memo_id = #{id}
AND member_no_login = #{memberNoLogin}

mapper.update:
UPDATE diary
SET diary_name = #{diaryName},
diary_content = #{diaryContent},
diary_image = #{diaryImage}
WHERE memo_id = #{memoId}
AND member_no_login = #{memberNoLogin}

mapper.delete:
DELETE FROM diary
WHERE memo_id = #{id}
AND member_no_login = #{memberNoLogin}
