mapper.insert:
INSERT INTO diary (
    member_no_login, diary_name, diary_content, diary_image, created_at
) VALUES (
             #{memberNoLogin}, #{diaryName}, #{diaryContent}, #{diaryImage}, NOW()
         )

mapper.select:
SELECT diary_id AS id, diary_name, diary_content, diary_image, created_at, member_no_login AS memberNoLogin FROM diary
member_no_login = #{memberNoLogin}
AND (diary_name LIKE #{pattern} OR diary_content LIKE #{pattern})
ORDER BY created_at DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM diary
member_no_login = #{memberNoLogin}
AND (diary_name LIKE #{pattern} OR diary_content LIKE #{pattern})

mapper.update:
UPDATE diary
SET
    diary_name = #{diaryName},
    diary_content = #{diaryContent},
    diary_image = #{diaryImage}
WHERE diary_id = #{id}
