mapper.select:
SELECT memo_id AS id, member_no_login, diary_name, diary_content, diary_image, created_at
FROM diary
WHERE memo_id = #{id}
  AND member_no_login = #{memberNoLogin}

mapper.update:
UPDATE diary
SET diary_name = #{diaryName},
    diary_content = #{diaryContent}
WHERE memo_id = #{id} AND member_no_login = #{memberNoLogin}

mapper.delete:
DELETE FROM diary
WHERE memo_id = #{id}
  AND member_no_login = #{memberNoLogin}

mapper.select:
SELECT memo_id AS id, member_no_login, diary_name, diary_content, diary_image, created_at
FROM diary
WHERE member_no_login = #{memberNoLogin}
ORDER BY memo_id DESC
    LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM diary
WHERE member_no_login = #{memberNoLogin}
