mapper.insert:
INSERT INTO memo (
member_no_login,
memo_name,
memo_content
) VALUES (
#{memberNoLogin},
#{title},
#{content}
)

mapper.update:
UPDATE memo
SET memo_image = #{memoImage}
WHERE memo_id = #{memoId}

mapper.select:
SELECT
memo_id AS id,
memo_name AS title,
memo_content AS content,
created_at,
memo_image AS imageFileName
FROM memo

    member_no_login = #{memberNoLogin}


        AND (
        (memo_name LIKE #{pattern}
        OR memo_content LIKE #{pattern})
        )


ORDER BY created_at DESC, memo_id DESC  LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM memo

    member_no_login = #{memberNoLogin}


    AND (
    memo_name LIKE #{pattern}
    OR memo_content LIKE #{pattern}
    )

mapper.update:
UPDATE memo
SET memo_name = #{title},
memo_content = #{content}
WHERE memo_id = #{id}
