mapper.insert:
INSERT INTO exerciselog( exercise_id, member_no, exercise_datetime, exercise_kcal, exercise_duration, effort_level)
VALUES(
      #{exerciseId}
    , #{memberNo}
    , #{exerciseDatetime}
    , #{exerciseKcal}
    , #{exerciseDuration}
    , #{effortLevel}
)

mapper.select:
SELECT b.exercise_name, a.exercise_duration, a.exercise_kcal, a.effort_level
  FROM exerciselog a
  JOIN exercise b
    ON a.exercise_id = b.exercise_id
 WHERE a.exerciselog_id = #{exerciselogId}
   AND a.member_no = #{memberNo};
