mapper.select:
SELECT COUNT(*)
FROM member_location_address
WHERE member_id = #{memberId}
AND road_address = #{roadAddress}
AND parcel_address = #{parcelAddress}

SELECT id, member_id, title, road_address, parcel_address
FROM member_location_address
WHERE member_id = #{memberId}

mapper.delete:
DELETE FROM member_location_address
WHERE id = #{id}
AND member_id = #{memberId}

mapper.update:
UPDATE member_location_address
SET is_selected = FALSE
WHERE member_id = #{memberId}

UPDATE member_location_address
SET is_selected = TRUE
WHERE id = #{addressId}
AND member_id = #{memberId}
